Client.Inventory=Client.Inventory or{}Client.Inventory.AddItem=function(a,b,c,d,e,f)print("^1Adding item is only available on the server^7")end;Client.Inventory.RemoveItem=function(a,b,c,d,e,f)print("^1Removing item is only available on the server^7")end;local g={}Client.Inventory.GetAllItems=function(a)if g[a]and GetGameTimer()-g[a].time<500 then local h=debug.getinfo(3).name=="fn"and debug.getinfo(2).name or debug.getinfo(3).name;warn(("Client.Inventory.%s is potentially being spammed, please use it less frequently. Cache the value if you need to."):format(h or"GetAllItems"))return g[a].value end;g[a]={value=Client.Callback(a,"lib:inventory:getAllItems"),time=GetGameTimer()}return g[a].value end;Client.Inventory.GetSlotsWithMetadata=function(a,i,c,e)local j={}for k=1,#i do if i[k].name==c then if not e or not next(e)then j[#j+1]=i[k]else local l=true;for m,n in pairs(e)do if i[k].metadata[m]~=n then l=false end end;if l then j[#j+1]=i[k]end end end end;return j end;Client.Inventory.GetCount=function(a,c,e)local o=0;local i=Client.Inventory.GetAllItems(a)if not i then return o end;local p=Client.Inventory.GetSlotsWithMetadata(a,i,c,e)for k=1,#p do o=o+(p[k].count or 0)end;return o end;Client.Inventory.HasItem=function(a,c,d,e)d=d or 1;return Client.Inventory.GetCount(a,c,e)>=d end;local q={}Client.Inventory.GetLabel=function(a,r)if q[r]then return q[r]end;q[r]=Client.Callback(a,"lib:inventory:getLabel",r)return q[r]end;Client.Inventory.ConvertToMenu=function(a,i,s)local t={}s=s or{}for k=1,#i do t[k]=Shared.Table.Clone(nil,s)t[k].title=("%s x %s"):format(i[k].name,i[k].count)t[k].itemData=i[k]if s.onSelect then t[k].onSelect=function()s.onSelect(i[k].name,i[k].count,i[k].metadata,t[k])end end end;return t end;Client.Inventory.GetAllItemsForMenu=function(a,s)local i=Client.Inventory.GetAllItems(a)return Client.Inventory.ConvertToMenu(a,i,s)end