if not IsStarted("Inventory","ox_inventory")then return end;local a=exports.ox_inventory;Lib.Inventory={HasItem=function(b,c,d)c=c or 1;local e=a:GetItemCount(b,d)return e>=c end,ItemCount=function(b,d)return a:GetItemCount(b,d)end,GetLabel=function(f)local b=a:Items(f)if b then return b.label end;return f end,GetWeight=function(g)if type(g)=="string"then g={g}end;local h=0;for i=1,#g do local b=a:Items(g[i])if b then h=h+b.weight end end;return h end,GetPlayerInventoryWeight=function()local j=a:GetPlayerItems()or{}local k=0;for i=1,#j do k=k+Lib.Inventory.GetWeight(j[i].name)*j[i].count end;return k end}