if not IsMissing("Inventory","ox_inventory")then return end;local a=exports.ox_inventory;Lib.Inventory={HasItem=function(b,c,d,e)d=d or 1;local f=a:GetItemCount(c,e)return f>=d end,ItemCount=function(b,c,e)return a:GetItemCount(c,e)end,GetLabel=function(b,g)local c=a:Items(g)if c then return c.label end;return g end,GetWeight=function(b,h)if type(h)=="string"then h={h}end;local i=0;for j=1,#h do local c=a:Items(h[j])if c then i=i+c.weight end end;return i end,GetPlayerInventoryWeight=function(b)local k=a:GetPlayerItems()or{}local l=0;for j=1,#k do l=l+Lib.Inventory.GetWeight(b,k[j].name)*k[j].count end;return l end}