if not IsMissing("Inventory","ox_inventory")then return end;local a=exports.ox_inventory;Lib.Inventory={HasItem=function(b,c,d,e,f)e=e or 1;local g=a:GetItemCount(c,d,f)return g>=e end,ItemCount=function(b,c,d,f)return a:GetItemCount(c,d,f)end,AddItem=function(b,c,d,e,f,h)e=e or 1;return a:AddItem(c,d,e,f,h)end,RemoveItem=function(b,c,d,e,f,h)e=e or 1;return a:RemoveItem(c,d,e,f,h)end,GetLabel=function(b,i)local d=a:Items(i)if d then return d.label end;return i end,GetWeight=function(b,j)if type(j)=="string"then j={j}end;local k=0;for l=1,#j do local d=a:Items(j[l])if d then k=k+d.weight end end;return k end,GetItemTableWeight=function(b,j)local k=0;for l=1,#j do k=k+Lib.Inventory.GetWeight(b,j[l].name)*j[l].count end;return k end,GetInventoryWeight=function(b,c)local m=a:GetInventoryItems(c)or{}local n=0;for l=1,#m do n=n+Lib.Inventory.GetWeight(b,m[l].name)*m[l].count end;return n end,GetPlayerInventoryWeight=function(b,c)return Lib.Inventory.GetInventoryWeight(b,c)end,GetMaxWeight=function(b,c)return tonumber(GetConvarInt('inventory:weight',30000))end}