local a,b=Lib.UseResource()if not a then return end;Lib.Inventory={HasItem=function(c,d,e,f)e=e or 1;local g=b:GetItemCount(d,f)return g>=e end,ItemCount=function(c,d,f)return b:GetItemCount(d,f)end,GetLabel=function(c,h)local d=b:Items(h)if d then return d.label end;return h end,GetWeight=function(c,i)if type(i)=="string"then i={i}end;local j=0;for k=1,#i do local d=b:Items(i[k])if d then j=j+d.weight end end;return j end,GetPlayerInventoryWeight=function(c)local l=b:GetPlayerItems()or{}local m=0;for k=1,#l do m=m+Lib.Inventory.GetWeight(c,l[k].name)*l[k].count end;return m end}