Lib.Audio={Play=function(a,b,c)WaitForNUI()if not a then return print("Resource not found.",a)end;SendNUIMessage({module="Audio",action="play",file=b,options=c,resource=a})end,Pause=function(a,b)WaitForNUI()if not a then return print("Resource not found.",a)end;SendNUIMessage({module="Audio",action="pause",file=b,resource=a})end,Stop=function(a,b)WaitForNUI()if not a then return print("Resource not found.",a)end;SendNUIMessage({module="Audio",action="stop",file=b,resource=a})end,StopAll=function(a)WaitForNUI()if not a then return print("Resource not found.",a)end;SendNUIMessage({module="Audio",action="stopAll",resource=a})end}