Lib.GetEntityCoords=function(a)local b=type(a)if b=="vector3"or b=="vector4"then return vector4(a.x,a.y,a.z,a.w or 0)end;if not DoesEntityExist(a)then return vector4(0,0,0,0)end;local c=GetEntityCoords(a)return vector4(c.x,c.y,c.z,GetEntityHeading(a))end;Lib.GetOffset=function(c,d)local e=Lib.GetEntityCoords(c)local f=e.w or 0;local g=math.rad(f)local h=d.x*math.cos(g)-d.y*math.sin(g)local i=d.x*math.sin(g)+d.y*math.cos(g)local j=e.x+h;local k=e.y+i;local l=e.z;local m=e.w;return vector4(j,k,l,m)end