Lib.GetCoordsFromIdEntityOrCoords=function(a)local b=type(a)if b=="vector3"then return a end;if b=="vector4"then return vector3(a.x,a.y,a.z)end;local c=a;if b=="number"then if not DoesEntityExist(a)then c=GetPlayerPed(a)end end;if not DoesEntityExist(c)then return vector3(0,0,0),print("Could not get coords")end;return GetEntityCoords(c)end;Lib.GetEntityCoords=function(d,c)local e=type(c)if e=="vector3"or e=="vector4"then return vector4(c.x,c.y,c.z,c.w or 0)end;if not DoesEntityExist(c)then return vector4(0,0,0,0)end;local f=GetEntityCoords(c)return vector4(f.x,f.y,f.z,GetEntityHeading(c))end;Lib.GetOffset=function(d,f,g)local h=Lib.GetEntityCoords(d,f)local i=h.w or 0;local j=math.rad(i)local k=g.x*math.cos(j)-g.y*math.sin(j)local l=g.x*math.sin(j)+g.y*math.cos(j)local m=h.x+k;local n=h.y+l;local o=h.z;local p=h.w;return vector4(m,n,o,p)end