Server.GetCoordsFromIdEntityOrCoords=function(a,b)local c=type(b)if c=="vector3"then return b end;if c=="vector4"then return vector3(b.x,b.y,b.z)end;local d=b;if c=="number"then if not DoesEntityExist(b)then d=GetPlayerPed(b)end end;if not DoesEntityExist(d)then return vector3(0,0,0),print("Could not get coords")end;return GetEntityCoords(d)end;Server.GetEntityCoords=function(a,d)local e=type(d)if e=="vector3"or e=="vector4"then return vector4(d.x,d.y,d.z,d.w or 0)end;if not DoesEntityExist(d)then d=GetPlayerPed(d)end;if not DoesEntityExist(d)then return vector4(0,0,0,0)end;local f=GetEntityCoords(d)return vector4(f.x,f.y,f.z,GetEntityHeading(d))end;Server.GetOffset=function(a,f,g)local h=Server.GetEntityCoords(a,f)local i=h.w or 0;local j=math.rad(i)local k=g.x*math.cos(j)-g.y*math.sin(j)local l=g.x*math.sin(j)+g.y*math.cos(j)local m=h.x+k;local n=h.y+l;local o=h.z+g.z;local p=i;return vector4(m,n,o,p)end