local a=function(b)local c={}local d={}for e,f in pairs(b)do d[#d+1]={key=e,value=f}c.keys=("%s%s%s"):format(c.keys or"",c.keys and", "or"",e)c.values=c.values or{}c.values[#c.values+1]=f;local g=type(f)=="number"and f or("'%s'"):format(tostring(f))c.set=("%s%s%s = %s"):format(c.set or"",c.set and", "or"",e,g)c.where=("%s%s%s = %s"):format(c.where or"",c.where and" AND "or"",e,g)end;local h=string.rep("?, ",#d,"")c.placeholders=h:sub(1,#h-2)return c,d end;Lib.Database={Insert=function(i,j,k)local c=a(k)local l=("INSERT INTO `%s` (%s) VALUES (%s)"):format(j,c.keys,c.placeholders)local m,n=MySQL.insert.await(l,c.values)if not m then print("Error inserting into database: "..n)end;return m end,Update=function(i,j,k,o)local c=a(k)local p=a(o)local l=("UPDATE `%s` SET %s WHERE %s"):format(j,c.set,p.where)local m,n=MySQL.query.await(l,c.values)if not m then print("Error updating database: "..n)end;return m end}