local a=function(b)local c={}local d={}for e,f in pairs(b)do d[#d+1]={key=e,value=f}c.keys=("%s%s%s"):format(c.keys or"",c.keys and", "or"",e)c.values=c.values or{}c.values[#c.values+1]=f;local g=type(f)=="number"and f or("'%s'"):format(tostring(f))c.set=("%s%s%s = %s"):format(c.set or"",c.set and", "or"",e,g)c.where=("%s%s%s = %s"):format(c.where or"",c.where and" AND "or"",e,g)end;local h=string.rep("?, ",#d,"")c.placeholders=h:sub(1,#h-2)return c,d end;Lib.Database={Insert=function(i,j)local c=a(j)local k=("INSERT INTO `%s` (%s) VALUES (%s)"):format(i,c.keys,c.placeholders)local l,m=MySQL.insert.await(k,c.values)if not l then print("Error inserting into database: "..m)end;return l end,Update=function(i,j,n)local c=a(j)local o=a(n)local k=("UPDATE `%s` SET %s WHERE %s"):format(i,c.set,o.where)local l,m=MySQL.query.await(k,c.values)if not l then print("Error updating database: "..m)end;return l end}