Lib.Loop=function(a,b,c,...)local d={...}local e,f=0,0;local g=GetGameTimer()if type(c)=="table"then e=c.timer;f=c.timeout end;local h=function()while true do b(table.unpack(d))if f and f>0 and GetGameTimer()-g>f then break end;if c.condition and type(c.condition)=="function"and c.condition()then break end;Wait(e)end end;if c.thread then Citizen.CreateThread(h)else h()end end