Lib.Loop=function(a,b,...)local c={...}local d,e=0,0;local f=GetGameTimer()if type(b)=="table"then d=b.timer;e=b.timeout end;local g=function()while true do a(table.unpack(c))if e and e>0 and GetGameTimer()-f>e then break end;if b.condition and type(b.condition)=="function"and b.condition()then break end;Wait(d)end end;if b.thread then Citizen.CreateThread(g)else g()end end