Lib.NearbyCoords=function(a,b,c)local d=Lib.GetCoordsFromIdEntityOrCoords(b)local e={}local f=GetActivePlayers()for g=1,#f do local h=f[g]local i=GetPlayerPed(h)local j=GetEntityCoords(i)local k=#(d-j)if k<=c then e[#e+1]=h end end;return e end;Lib.RunOnNearby=function(a,l,c,m)local e=Lib.NearbyCoords(a,l,c)for g=1,#e do m(e[g])end end;Lib.Nearby={Notify=function(a,l,c,...)local n={...}Lib.RunOnNearby(nil,l,c,function(o)Lib.Notify(a,o,table.unpack(n))end)end,ChatMessage=function(a,l,c,...)local n={...}Lib.RunOnNearby(nil,l,c,function(o)TriggerClientEvent("chat:addMessage",o,table.unpack(n))end)end}