local a=nil;Client.PowerBuildup=function(b,c,d,e)if a then a:resolve(nil)end;a=promise.new()local f;local g=-1;Client.DrawText(b,"Power buildup: standby.")CreateThread(function()while a do if IsControlPressed(0,c)then if not f then f=GetGameTimer()end;local h=math.floor((GetGameTimer()-f)/1000)if h~=g then g=h;Client.DrawText(b,"Power: "..g)end end;if f and not IsControlPressed(0,c)then local i=GetGameTimer()-f;a:resolve(i)a=nil;f=nil;return end;Wait(0)end end)local j=Citizen.Await(a)Client.DrawText(b)if d then j=math.max(d*1000 or 0,j)end;if e then j=math.min(e*1000,j)end;return j end