Lib.Fetch=function(a,b)local c=promise.new()PerformHttpRequest(b,function(d,e,f)if d==200 then c:resolve(json.decode(e))else c:resolve(nil)end end,"GET","",{["Content-Type"]="application/json"})return Citizen.Await(c)end;Lib.VersionCheck=function(a,g,h)if not(g and h)then print(("Resource Version Check, invoked from %s, is missing org or repo"):format(a))return false,"Missing org or repo"end;local b="https://api.github.com/repos/"..g.."/"..h.."/tags"local i=Lib.Fetch(a,b)if i then local j=i[1].name;local k=GetResourceMetadata(a,"version")k=("v%s"):format(k)if j~=k then print("Resource "..a.." is outdated. Current: "..k.." Latest: "..j)end;return j==k,j,k end;return true,"Couldn't get version"end