Lib.SpawnPed=function(a,b)assert(Lib.assertEntityData(a,b))local c=Lib.LoadModel(a,b.model)local d=vector4(b.coords.x,b.coords.y,b.coords.z,b.coords.w or 0.0)local e=b.networked or false;local f=CreatePed(4,c,d.x,d.y,d.z,d.w,e,false)AddToStore("ENTITIES",a,f)SetModelAsNoLongerNeeded(c)FreezeEntityPosition(f,b.freeze)if b.blockEvents then SetBlockingOfNonTemporaryEvents(f,true)end;if b.invincible then SetEntityInvincible(f,true)end;if b.defaultModel then SetPedDefaultComponentVariation(f)end;if b.animation then if DoesAnimDictExist(b.animation.dict)then RequestAnimDict(b.animation.dict)while not HasAnimDictLoaded(b.animation.dict)do Wait(0)end;TaskPlayAnim(f,b.animation.dict,b.animation.clip,8.0,8.0,-1,1,0,false,false,false)end end;if b.targets and Lib.Target.AddEntity then Lib.Target.AddEntity(a,f,b.targets,resource)end;return f end